    .navbar-nav .nav-link:hover {
        color: var(--primary) !important;
        border-radius: 5px;
        font-weight: 500;
    }


    .navbar-nav .nav-link.active {
        color: var(--white) !important;
        border-radius: 5px;
        font-weight: 500;
        background: var(--primary);
        padding: 8px;
    }

    .contactlink {
        background-color: var(--bs-yellow) !important;
        border-radius: 5px;
    }


